        /// </returns>
        public override Int32 Create(AddressDto addressDto)
        {

            #region Preconditions

            // Comprobar el DTO de entrada.
            Guard.ArgumentIsNotNull(
                                    addressDto,
                                    string.Format(
                                                    FrameworkResource.DataTransferObjectIsNull,
                                                    CommonResources.AddressAlias));
            // Comprobar los campos mandatory dentro del DTO.
            Guard.ArgumentNotNullOrEmpty(
                                        addressDto.StreetName,
                                        string.Format(
                                                        FrameworkResource.PropertyRequired,
                                                        CommonResources.Address_StreetNameAlias, 
                                                        CommonResources.AddressAlias)
                                        );

            #endregion

            #region Preconditions from composition sources
                Guard.ArgumentIsNotNull(
                                    addressDto.UserAddress,
                                    string.Format(
                                                FrameworkResource.DataTransferObjectIsNull,
                                                CommonResources.Address_UserAddressAlias_));

            #endregion
            // .en the dto has to be transient, so the Id has to have a default value.
            // .es el dto debe corresponder a un transient por lo que el id debe tener el valor por defecto
            Guard.Against<ArgumentException>(addressDto.Id != default(Int32),                                    
                                                    string.Format(
                                                                FrameworkResource.IsNotTransient,
                                                                CommonResources.AddressAlias
                                                                    )
                                            );

            IUserRepository userRepo = ApplicationLayer.IocContainer.Resolve<IUserRepository>();
            User userAddress = userRepo.GetById(addressDto.UserAddress.Id);

            Address address = AddressFactory.Create
            (
                addressDto.StreetName, userAddress
            );//*#*
            address.BuildingNumber = addressDto.BuildingNumber; // property.AutoProperty = True; property.OnlyGetProperty = False
                //.en Getting properties that comes from parent relationships
                //.es Obtenemos las propiedades que provienen the relaciones parentales.
            if (addressDto.UserAddress != null)
            {
                IUserRepository repouser = ApplicationLayer.IocContainer.Resolve<IUserRepository>();
                var userTemp = repouser.GetById(addressDto.UserAddress.Id);
                address.SetUserAddress(userTemp);
            }
            this.EntityRepository.Add(address);
            this.Commit();

            return address.Id;
        }
