        /// <summary>
        /// Actualiza una determinada entidad Address.
        /// </summary>
        /// <param name="addressDto">
        /// DTO que contiene la información de la entidad que se va a actualizar.
        /// </param>
        /// <returns>
        /// Es <b>true</b> si la actualización ha sido correcta; en caso contrario <b>false</b>.
        /// </returns>
        public override bool Update(AddressDto addressDto)
        {
            #region preconditions
            // Comprobar el DTO de entrada. 
            Guard.ArgumentIsNotNull(
                                    addressDto,
                                    string.Format(
                                                  FrameworkResource.DataTransferObjectIsNull,
                                                  "Address"));
            // en una actualización no comprobamos los campos mandatory.
            #endregion            

            try
            {
                // Obtener y comprobar validez de la inserción a modificar.
                Address entity2Update = this.EntityRepository.GetFilteredElements(t => t.Id == addressDto.Id).Single();
                Guard.ArgumentIsNotNull(
                                        entity2Update,
                                        string.Format(
                                                        FrameworkResource.CanNotUpdateInexistenceEntity,
                                                        "Address"));
                // Comprobar duplicidades;

                    // Actualización de la entidad.
                    entity2Update.StreetName = addressDto.StreetName;
                    entity2Update.BuildingNumber = addressDto.BuildingNumber;
                    // .en Updating properties that comes from parent relationships
                    // .es Actualizamos las propiedades que provienen the relaciones parentales.
                    if (addressDto.UserAddress != null && !addressDto.UserAddress.IsTransient() /*&& entity2Update.UserAddress.Id != addressDto.UserAddress.Id*/)
                    {
                        IUserRepository repouser = ApplicationLayer.IocContainer.Resolve<IUserRepository>();//**
                        User user = repouser.GetById(addressDto.UserAddress.Id);
                        entity2Update.SetUserAddress(user);
                    }
                    else if(addressDto.UserAddress == null)
                    {
                        entity2Update.SetUserAddress(null);
                    }

                // igualmente hemos de mapear las entidades emparentadas.

                this.EntityRepository.Modify(entity2Update);
                this.Commit();
            }
            catch (Exception ex)
            {
                throw ex;
            }

            // Devolvemos el resultado.
            return true;
        }
