                department.CreationTime = departmentDto.CreationTime; // property.AutoProperty = True; property.OnlyGetProperty = False
                department.UpdateTime = departmentDto.UpdateTime; // property.AutoProperty = True; property.OnlyGetProperty = False
            IDepartmentRepository repo =  ApplicationLayer.IocContainer.Resolve<IDepartmentRepository>();
            repo.Add(department);
            this.Commit();

            return department.Id; 
        }
