        #region Service Delete
        /// <summary>
        /// Elimina una determinada entidad Department.
        /// </summary>
        /// <param name="id">
        /// Identificador de la entidad que se va a eliminar.
        /// </param>
        /// <returns>
        /// Es <b>true</b> si la eliminación ha sido correcta; en caso contrario <b>false</b>.
        /// </returns>
        public bool Delete(int id)
        {
            IDepartmentRepository repo = ApplicationLayer.IocContainer.Resolve<IDepartmentRepository>();
            IEnumerable<Department> results = repo.GetFilteredElements(u => u.Id == id);
            Department department2Delete = results.First();
            if (!department2Delete.CanBeDeleted())
            {
                return false;
            }
            repo.Remove(department2Delete);
            this.Commit();

            return true;
        }
