        /// <summary>
        /// Actualiza una determinada entidad Department.
        /// </summary>
        /// <param name="departmentDto">
        /// DTO que contiene la información de la entidad que se va a actualizar.
        /// </param>
        /// <returns>
        /// Es <b>true</b> si la actualización ha sido correcta; en caso contrario <b>false</b>.
        /// </returns>
        public bool Update(DepartmentDto departmentDto)
        {
            #region preconditions
            // Comprobar el DTO de entrada. 
            Guard.ArgumentIsNotNull(
                                    departmentDto,
                                    string.Format(
                                                  FrameworkResource.DataTransferObjectIsNull,
                                                  "Department"));
            // en una actualización no comprobamos los campos mandatory.
            #endregion            

            try
            {
                // Creamos el repositorio de la entidad.
                IDepartmentRepository repoDepartment = ApplicationLayer.IocContainer.Resolve<IDepartmentRepository>();

                // Obtener y comprobar validez de la inserción a modificar.
               Department entity2Update = repoDepartment.GetFilteredElements(t => t.Id == departmentDto.Id).Single();
                Guard.ArgumentIsNotNull(
                                        entity2Update,
                                        string.Format(
                                                        FrameworkResource.CanNotUpdateInexistenceEntity,
                                                        "Department"));
                // Comprobar duplicidades;

                    // Actualización de la entidad.
                    // Mandatory data
                    entity2Update.Name = departmentDto.Name;
                    // asignación de Datos no mandatory con ciclo de vida variable, no se incluyen aquellas propiedaes constantes o derivadas
                    entity2Update.Visible = departmentDto.Visible; // property.AutoProperty = False; property.OnlyGetProperty = False
                    entity2Update.Description = departmentDto.Description; // property.AutoProperty = True; property.OnlyGetProperty = False
                    entity2Update.CreationTime = departmentDto.CreationTime; // property.AutoProperty = True; property.OnlyGetProperty = False
                    entity2Update.UpdateTime = departmentDto.UpdateTime; // property.AutoProperty = True; property.OnlyGetProperty = False

                // igualmente hemos de mapear las entidades emparentadas.
                if (!entity2Update.CanBeSaved())
                {
                    return false;
                }
                repoDepartment.Modify(entity2Update);

                // Confirmamos la transacción.
                this.Commit();
                
            }
            catch (Exception ex)
            {
                throw ex;
            }

            // Devolvemos el resultado.
            return true;
        }
