        public override StudentDto GetById(Int32 id)
        {
            // Variable de respuesta.
            StudentDto entityDto = null;

            try
            {               
                var entity = this.EntityRepository.GetAggregateById(id); //TODO: call async
                entityDto = this.EntityMapper.EntityMapping(entity, true, true);

                foreach (var item in entity.Teachers)
                {
                    var teacher = this.teacherMapper.EntityMapping(item);
                    entityDto.Teachers.Add(teacher);
                }

            }
            catch (Exception ex)
            {
                throw ex;
            }
            finally
            {
                // Confirmamos la transacción.
                this.Commit();
            }

            // Devolvemos el resultado.
            return entityDto;
        }
