        /// <summary>
        /// Crea una entidad User.
        /// </summary>
        /// <param name="userDto">
        /// DTO que contiene la información para crear la entidad.
        /// </param>
        /// <returns>
        /// El identificador de la entidad creada.
        /// </returns>
        public override Int32 Create(UserDto userDto)
        {

            #region Preconditions

            // Comprobar el DTO de entrada.
            Guard.ArgumentIsNotNull(
                                    userDto,
                                    string.Format(
                                                    FrameworkResource.DataTransferObjectIsNull,
                                                    CommonResources.UserAlias));
            // Comprobar los campos mandatory dentro del DTO.
            Guard.ArgumentNotNullOrEmpty(
                                        userDto.Name,
                                        string.Format(
                                                        FrameworkResource.PropertyRequired,
                                                        CommonResources.User_NameAlias, 
                                                        CommonResources.UserAlias)
                                        );

            #endregion

            #region Preconditions from composition sources
                Guard.ArgumentIsNotNull(
                                    userDto.UserDepartment,
                                    string.Format(
                                                FrameworkResource.DataTransferObjectIsNull,
                                                CommonResources.User_UserDepartmentAlias_));

            #endregion
            // .en the dto has to be transient, so the Id has to have a default value.
            // .es el dto debe corresponder a un transient por lo que el id debe tener el valor por defecto
            Guard.Against<ArgumentException>(userDto.Id != default(Int32),                                    
                                                    string.Format(
                                                                FrameworkResource.IsNotTransient,
                                                                CommonResources.UserAlias
                                                                    )
                                            );

            IDepartmentRepository departmentRepo = ApplicationLayer.IocContainer.Resolve<IDepartmentRepository>();
            Department userDepartment = departmentRepo.GetById(userDto.UserDepartment.Id);
            var userAddress = new System.Collections.Generic.List<Address>();


            User user = UserFactory.Create
            (
                userDto.Name, userDepartment, userAddress
            );//**
            foreach (var item in userDto.Addresses)
            {
                var temp = AddressFactory.Create(item.StreetName, user);//*
                temp.BuildingNumber = item.BuildingNumber;
                user.Addresses.Add(temp);
            }
                //.en Getting properties that comes from parent relationships
                //.es Obtenemos las propiedades que provienen the relaciones parentales.
            if (userDto.UserDepartment != null)
            {
                IDepartmentRepository repodepartment = ApplicationLayer.IocContainer.Resolve<IDepartmentRepository>();
                var departmentTemp = repodepartment.GetById(userDto.UserDepartment.Id);
                user.SetUserDepartment(departmentTemp);
            }
            this.EntityRepository.Add(user);
            this.Commit();

            return user.Id;
        }
