        #region Protected Methods
        // .en implement here the loading of related entities or value objects.
        // .es implementar aqui los metodos de carga  de entidades u objetos valor relacionadas al root aggregate.

        private void PopulateUsers(Int32 filterId, EnumPopulationType populationMethod = EnumPopulationType.All)
        {
            UserServiceClient serviceClient = new UserServiceClient();
            userAddressVisible = true;

            if (this.UsersCombo != null && this.UsersCombo.Count() > 0)
            {
                this.UsersCombo.Clear();
            }

            if (filterId.Equals(default(Int32)) && populationMethod.HasFlag(EnumPopulationType.All))
            {
                this.UsersCombo = serviceClient.GetAll();
            }
            else if (populationMethod.HasFlag(EnumPopulationType.AllExceptOwnId))
            {
                this.UsersCombo = serviceClient.GetAllExceptThis(filterId); // Get All except the own Id to avoid a self reference object
            }
            else if (populationMethod.HasFlag(EnumPopulationType.OnlyId))
            {
                if (this.UserAddress != null && this.UserAddress.Id == filterId)
                {
                    this.usersCombo = new ObservableCollection<UserDto>() { this.UserAddress };
                }
                else
                {
                    this.UserAddress = serviceClient.GetById(filterId);
                    this.UsersCombo = new ObservableCollection<UserDto>() { this.UserAddress };
                }

                userAddressVisible = !this.from1VM;
            }

            if (this.UsersCombo != null && populationMethod.HasFlag(EnumPopulationType.AddNullOrNoneOption))
            {
                this.UsersCombo.Insert(0, this.UserAddress != null && !this.UserAddress.IsTransient() ? this.UserAddress : new UserDto());
            }

            if (populationMethod.HasFlag(EnumPopulationType.OnlyId))
                this.allUsersQueryVM = new UserQueryViewModel(typeof(AddressView).FullName);
            else
                this.allUsersQueryVM = new UserQueryViewModel(this.UsersCombo, typeof(AddressView).FullName);

            userComboVisible = this.UsersCombo.Count() <= 50;
        }


