        // .en implement here the loading of related entities or value objects.
        // .es implementar aqui los metodos de carga  de entidades u objetos valor relacionadas al root aggregate.

        private void PopulateComponents(Int32 filterId, EnumPopulationType populationMethod = EnumPopulationType.All)
        {
            ComponentServiceClient serviceClient = new ComponentServiceClient();
            parentVisible = true;

            if (this.ComponentsCombo != null && this.ComponentsCombo.Count() > 0)
            {
                this.ComponentsCombo.Clear();
            }

            if (filterId.Equals(default(Int32)) && populationMethod.HasFlag(EnumPopulationType.All))
            {
                this.ComponentsCombo = serviceClient.GetAll();
            }
            else if (populationMethod.HasFlag(EnumPopulationType.AllExceptOwnId))
            {
                this.ComponentsCombo = serviceClient.GetAllExceptThis(filterId); // Get All except the own Id to avoid a self reference object
            }
            else if (populationMethod.HasFlag(EnumPopulationType.AllExceptOwnIdAndChildren))
            {
                this.componentsCombo = serviceClient.GetAllExceptIdAndChildren(filterId); // Get All except the own Id to avoid a self reference object
            }
            else if (populationMethod.HasFlag(EnumPopulationType.OnlyId))
            {
                if (this.Parent != null && this.Parent.Id == filterId)
                {
                    this.componentsCombo = new ObservableCollection<ComponentDto>() { this.Parent };
                }
                else
                {
                    this.Parent = serviceClient.GetById(filterId);
                    this.ComponentsCombo = new ObservableCollection<ComponentDto>() { this.Parent };
                }

                parentVisible = !this.from1VM;
            }

            if (this.ComponentsCombo != null && populationMethod.HasFlag(EnumPopulationType.AddNullOrNoneOption))
            {
                this.ComponentsCombo.Insert(0, this.Parent != null && !this.Parent.IsTransient() ? this.Parent : new ComponentDto());
            }

            if (populationMethod.HasFlag(EnumPopulationType.OnlyId))
                this.allComponentsQueryVM = new ComponentQueryViewModel(typeof(ComponentView).FullName);
            else
                this.allComponentsQueryVM = new ComponentQueryViewModel(this.ComponentsCombo, typeof(ComponentView).FullName);

            componentComboVisible = this.ComponentsCombo.Count() <= 50;
        }


