        /// <summary>
        /// .en Here is possible to override the actions to execute when there is a navigation request
        /// </summary>
        /// <remarks>
        ///  this logic applys when you came from the collection of users through double click
        ///  load parent entities and populate the related combobox with unique value
        /// .en Here is where to call to the methods to load the related entities (children and parents).
        /// .es aqui es donde se invoca a los métodos de carga de las entidades adicionales.
        /// </remarks>
        public override void OnNavigatedTo(Microsoft.Practices.Prism.Regions.NavigationContext navigationContext)
        {
            base.OnNavigatedTo(navigationContext);
            Int32 parentComponentId = default(Int32);

            if (navigationContext.Parameters["Id"] != default(Int32).ToString())//#
            {
                var parameter = navigationContext.Parameters["Id"];
                if (parameter != null)
                {
                    parentComponentId = Int32.Parse(parameter.Split(';').Last());
                }
            }

            // populate the combobox
            if (((this.Parent == null && parentComponentId == default(Int32)) || (this.Parent != null && this.Parent.Id == default(Int32))) && parentComponentId == default(Int32))
            {
                // with all values except own
                PopulateComponents(this.Parent.Id, EnumPopulationType.AllExceptOwnIdAndChildren | EnumPopulationType.AddNullOrNoneOption);
            }
            else
            {
                // only with the parent
                parentComponentId = this.Parent != null && this.Parent.Id != default(Int32) ? this.Parent.Id : parentComponentId;
                PopulateComponents(parentComponentId, EnumPopulationType.OnlyId | EnumPopulationType.AddNullOrNoneOption); // get only the parent
            }

            //.en load children entities
            //.es cargar aqui los agregados relacionados

           Initialization(this.ObjectElement);

            if (navigationContext.Parameters["Id"] != default(Int32).ToString())//**
            {
                // only in this case there is a need of subscription to update a parent view
                if (!CompositeViewUpdateEvent.Contains(this.OnReceiveCompositeViewUpdateEvent))
                {
                    CompositeViewUpdateEvent.Subscribe(this.OnReceiveCompositeViewUpdateEvent, ThreadOption.UIThread);
                }
            }

            //ComponentServiceClient componentServiceClient = new ComponentServiceClient();
            var specDto = new SpecificationDto() { PageIndex = 0, PageSize = 5 };
            specDto.CompositeFilter.Filters.Add(new Filter() { Property = "Parent.Id", Operator = "IsEqualTo", Value = navigationContext.Parameters["Id"] });//this.ObjectElement.Id.ToString()
            ChildrenQueryVM = new ComponentQueryViewModel(specDto, typeof(ComponentView).FullName);
            this.ChildrenQueryVM.Rebind();
            this.Rebind();
        }

