        // .en implement here the loading of related entities or value objects.
        // .es implementar aqui los metodos de carga  de entidades u objetos valor relacionadas al root aggregate.

        private void PopulateDepartments(Int32 filterId, EnumPopulationType populationMethod = EnumPopulationType.All)
        {
            DepartmentServiceClient serviceClient = new DepartmentServiceClient();
            userDepartmentVisible = true;

            if (this.DepartmentsCombo != null && this.DepartmentsCombo.Count() > 0)
            {
                this.DepartmentsCombo.Clear();
            }

            if (filterId.Equals(default(Int32)) && populationMethod.HasFlag(EnumPopulationType.All))
            {
                this.DepartmentsCombo = serviceClient.GetAll();
            }
            else if (populationMethod.HasFlag(EnumPopulationType.AllExceptOwnId))
            {
                this.DepartmentsCombo = serviceClient.GetAllExceptThis(filterId); // Get All except the own Id to avoid a self reference object
            }
            else if (populationMethod.HasFlag(EnumPopulationType.OnlyId))
            {
                if (this.UserDepartment != null && this.UserDepartment.Id == filterId)
                {
                    this.departmentsCombo = new ObservableCollection<DepartmentDto>() { this.UserDepartment };
                }
                else
                {
                    this.UserDepartment = serviceClient.GetById(filterId);
                    this.DepartmentsCombo = new ObservableCollection<DepartmentDto>() { this.UserDepartment };
                }

                userDepartmentVisible = !this.from1VM;
            }

            if (this.DepartmentsCombo != null && populationMethod.HasFlag(EnumPopulationType.AddNullOrNoneOption))
            {
                this.DepartmentsCombo.Insert(0, this.UserDepartment != null && !this.UserDepartment.IsTransient() ? this.UserDepartment : new DepartmentDto());
            }

            if (populationMethod.HasFlag(EnumPopulationType.OnlyId))
                this.allDepartmentsQueryVM = new DepartmentQueryViewModel(typeof(UserView).FullName);
            else
                this.allDepartmentsQueryVM = new DepartmentQueryViewModel(this.DepartmentsCombo, typeof(UserView).FullName);

            departmentComboVisible = this.DepartmentsCombo.Count() <= 50;
        }


